import React, { useState, useRef } from 'react';
import './DragAndDrop.css';
import addimage from '../images/addimage2.png'
const DragAndDrop = ({ onFilesSelected }) => {
  const [highlight, setHighlight] = useState(false);
  const [uploadedFiles, setUploadedFiles] = useState([]); // State to track uploaded files
  const fileInputRef = useRef(null);

  const handleDragOver = (event) => {
    event.preventDefault();
    setHighlight(true);
  };

  const handleDragLeave = () => {
    setHighlight(false);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setHighlight(false);

    const files = event.dataTransfer.files;
    const filteredFiles = filterFiles(Array.from(files));
    setUploadedFiles(filteredFiles); // Update the state with uploaded files
    onFilesSelected(filteredFiles);
  };

  const openFileDialog = () => {
    fileInputRef.current.click();
  };

  const handleFileInputChange = (event) => {
    const files = event.target.files;
    const filteredFiles = filterFiles(Array.from(files));
    setUploadedFiles(filteredFiles); // Update the state with uploaded files
    onFilesSelected(filteredFiles);
  };

  // Filter files to include only JPEG and PNG images
  const filterFiles = (files) => {
    return files.filter(file => {
      const fileType = file.type;
      return fileType === 'image/jpeg' || fileType === 'image/png';
    });
  };

  return (
    <div
      className={`drag-drop-area ${highlight ? 'highlight' : ''}`}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
      onClick={openFileDialog}
    >
      <img src={addimage} alt="image failed to load"/>
      <input
        ref={fileInputRef}
        className="file-input"
        type="file"
        onChange={handleFileInputChange}
        accept=".jpg,.jpeg,.png" // Limit file types to JPEG and PNG
        multiple
      />
      <div className="drag-drop-text">
        <p><span className="span1">Drag and drop</span></p>
        <p> or <span className="span2">upload</span> your image here.</p>
      </div>
      {/* Display uploaded files */}
      {uploadedFiles.length > 0 && (
        <div className="uploaded-files">
          <h3>Uploaded Images:</h3>
          <ul>
            {uploadedFiles.map((file, index) => (
              <li key={index}>{file.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default DragAndDrop;
